     */
    function edit_utility_billing_head($id = '')
    {
        $data = array(
            'title' => "Utility Service Provider"
        );
        if($id == ''){
            $id = $this->input->post('up_billing_head_id');
        }
        $uplan_id = $this->uri->segment(3);   
        // check if the up_billing_head exists before trying to edit it
        $data['up_billing_head'] = $this->Up_billing_head_model->get_up_billing_head($id);
        
        if(isset($data['up_billing_head']['id']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'is_unit_value' => $this->input->post('utility_unit_value'),
					'utility_service_provider_id' => $this->input->post('usp_id'),
					'utility_plan_id' => $this->input->post('uplan_id'),
					// 'id' => $this->input->post('id'),
					'name' => $this->input->post('utility_bill_head_name'),
					'amount' => $this->input->post('utility_bill_head_amount'),
					// 'created_at' => $this->input->post('created_at'),
					'updated_at' => $this->input->post('updated_at'),
                );
                // echo $id;die;
                $this->Up_billing_head_model->update_up_billing_head($id,$params);           
                redirect('utility_billing_head/view'.'/'.$params['utility_plan_id'].'/'.$params['utility_service_provider_id']);
            }
            else
            {
                $data['_view'] = 'up_billing_head/edit';
