    /*
     * Deleting up_billing_head
     */
    function remove($id)
    {
        $id =  $this->uri->segment(3);
        $uplan_id = $this->uri->segment(4);
        $usp_id = $this->uri->segment(5);
        $up_billing_head = $this->Up_billing_head_model->get_up_billing_head($id);

        // check if the up_billing_head exists before trying to delete it
        if(isset($up_billing_head['id']))
        {
            $this->Up_billing_head_model->delete_up_billing_head($id);
            redirect('utility_billing_head/view'.'/'.$uplan_id.'/'.$usp_id);
