        $usp_data = $data['utility_provider_members'] = $this->Utility_service_provider_member_model->get_all_utility_service_provider_member($usp_id);
        // $data['member'] = $member;
        $member_data_arr = array();
        $i = 0;
        foreach($usp_data as $abc)
        {
            $member_id = $abc['member_id'];
            $member_data = $this->Utility_service_provider_member_model->get_members_by_id_uspm($member_id);

            if($member_data){
                $member_data_arr[$i] = $member_data[0];
            }

        //echo "<pre>";print_r($data['member_data']);
