    /*
     * Editing a utility_service_provider_member
     */
    function edit_member($utility_service_provider_id)
    {   
        // check if the utility_service_provider_member exists before trying to edit it
        $data['utility_provider_member'] = $this->Utility_service_provider_member_model->get_utility_service_provider_member($utility_provider_id);
        
        if(isset($data['utility_provider_member']['utility_provider_id']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'utility_plan_id' => $this->input->post('utility_plan_id'),
					'id' => $this->input->post('id'),
					'member_id' => $this->input->post('member_id'),
					'created_at' => $this->input->post('created_at'),
					'updated_at' => $this->input->post('updated_at'),
					'member_balance' => $this->input->post('member_balance'),
					'initial_outstanding' => $this->input->post('initial_outstanding'),
                );

                $this->Utility_service_provider_member_model->update_utility_service_provider_member($utility_provider_id,$params);            
                redirect('utility_provider_member/manage');
            }
            else
            {
                $data['_view'] = 'utility_provider_member/edit';
                $this->load->view('utility_provider/members/edit_member', $data);
            }
        }
        else
            show_error('The utility_service_provider_member you are trying to edit does not exist.');
    }

