    /*
     * Deleting utility_service_provider_member
     */
    function remove($utility_provider_id)
    {
        $utility_provider_member = $this->Utility_service_provider_member_model->get_utility_service_provider_member($utility_provider_id);

        // check if the utility_service_provider_member exists before trying to delete it
        if(isset($utility_provider_member['utility_service_provider_id']))
        {
            $this->Utility_service_provider_member_model->delete_utility_service_provider_member($utility_service_provider_id);
            redirect('utility_provider_member/manage_member');
        }
        else
            show_error('The utility_service_provider_member you are trying to delete does not exist.');
    }

    public function assign_plan() {
        $data = array(
            'title' => "Utility Service Provider"
        );

        if($this->input->post()){
            $post = $this->input->post('post_data');
            // print_r($post);die;
