                {
                    $member_id = $abc['member_id'];
                    $member_data = $this->Utility_service_provider_member_model->get_members_by_id_uspm($member_id);

                    if($member_data){
                        $member_data_arr[$i] = $member_data[0];
                    }

            //echo "<pre>";print_r($data['member_data']);
                $i++;
                }
                $data['member_data'] = $member_data_arr;
                $data['utility_plans'] = $this->Utility_plan_model->get_all_utility_plan($usp_id);
                redirect('utility_provider_member/manage'.'/'.$usp_id);
                // echo "<pre>";print_r($$usp_data);die;
                       
            // }
            // die;
        }
        else
        {
            $usp_id = $data['usp_id'] = $this->uri->segment(3);
            $usp_data = $data['utility_provider_member'] = $this->Utility_service_provider_member_model->get_all_utility_service_provider_member($usp_id);
            $member_data_arr = array();
            $i = 0;
            foreach($usp_data as $abc)
            {
                $member_id = $abc['member_id'];
                $member_data = $this->Utility_service_provider_member_model->get_members_by_id_uspm($member_id);

                if($member_data){
                    $member_data_arr[$i] = $member_data[0];
                }

            //echo "<pre>";print_r($data['member_data']);
