    /*
     * Get society_access by id
     */
    function get_society_access($id)
    {
        return $this->db->get_where('society_accesses',array('id'=>$id))->row_array();
    }
        
    /*
     * Get all society_accesses
     */
    function get_all_society_accesses()
    {
        $this->db->order_by('id', 'desc');
        return $this->db->get('society_accesses')->result_array();
    }

    function get_all_society_accesses_by_user($user_id)
    {
        $this->db->where('user_id', $user_id);
        $this->db->where('society_id >', 0);
        $this->db->select('society_id');
        $this->db->from('society_accesses');
        $this->db->order_by('id', 'desc');
        return $this->db->get()->result_array();
    }

    function get_master_cp_societies($cp_user_id)
    {
        $this->db->where_in('user_id', $cp_user_id);
        $this->db->select('society_id');
        $this->db->from('society_accesses');
        $this->db->order_by('id', 'desc');
        return $this->db->get()->result_array();
    }

    function get_cp_master_flats($total_flats_cp)
    {
        $this->db->where_in('society_id', $total_flats_cp);
        $this->db->select('id');
        $this->db->from('members');
        $this->db->order_by('id', 'desc');
        return $this->db->get()->result_array();
    }
        
    /*
     * function to add new society_access
     */
    function add_society_access($params)
    {
        $this->db->insert('society_accesses',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update society_access
     */
    function update_society_access($id,$params)
    {
        $this->db->where('id',$id);
        return $this->db->update('society_accesses',$params);
    }
    
    /*
     * function to delete society_access
     */
    function delete_society_access($id)
    {
        return $this->db->delete('society_accesses',array('id'=>$id));
    }

    function get_all_society_accesses_by_user_id($user_id)
    {
        $this->db->distinct();
        $this->db->select('society_id');
        $this->db->where('user_id', $user_id);
        $this->db->from('society_accesses');
        $this->db->order_by('id', 'desc');
