    function check_existing_access($user_id,$role_id,$society_id)
    {
        $this->db->select('*');
        $this->db->where('user_id', $user_id);
        $this->db->where('role_id', $role_id);
        $this->db->where('society_id', $society_id);
        $this->db->from('society_accesses');
        return $this->db->get()->result_array();
    }
    // Auther - sachhidanand
    // function used to get list of society access -14-03-2022

    function society_access_list($user_id=0){
        $this->db->select('societies.*,count(members.id) as flat');       
        $this->db->from('societies');
        $this->db->join('members', 'members.society_id = 
        societies.id', 'left');
        $this->db->join('society_accesses', 'society_accesses.society_id = 
        societies.id', 'left');
        $this->db->where('society_accesses.user_id', $user_id);
        $this->db->group_by("societies.id");
        return $this->db->get()->result_array();
    }

